# Translating RNA into Protein
sample = ''

# Read codon table file
rna_codon_table_file = open("RNA-codon-table.txt")
rna_codon_table = rna_codon_table_file.read()

# Convert codon table to lookup dictionary
rna_codon_list = list(filter(None,rna_codon_table.replace('   ','\n').split('\n')))
rcd = dict(map(lambda e: e.split(' '), rna_codon_list))

# Split sample into list of triples
sample_split = [sample[i:i+3] for i in range(0, len(sample),3)]

# Translate the sample using table
result = ''.join(rcd[e] for e in sample_split).split('Stop')[0]

print(result)


